SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET AUTOCOMMIT = 0;
START TRANSACTION;
SET time_zone = "+00:00";



CREATE TABLE `cargo` (
  `id_cargo` int(11) NOT NULL,
  `cargo` varchar(100) COLLATE latin1_general_ci NOT NULL,
  `descricao_cargo` text COLLATE latin1_general_ci NOT NULL,
  `salario_inicial` float NOT NULL,
  `salario_medio` float NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci;





CREATE TABLE `contrato` (
  `num_contrato` int(11) NOT NULL,
  `regime` varchar(100) COLLATE latin1_general_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci;



CREATE TABLE `endereco` (
  `codigo_endereco` int(11) NOT NULL,
  `bairro` varchar(100) COLLATE latin1_general_ci NOT NULL,
  `cidade` varchar(100) COLLATE latin1_general_ci NOT NULL,
  `uf` varchar(2) COLLATE latin1_general_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci;






CREATE TABLE `posts` (
  `id_posts` int(11) NOT NULL,
  `titulo` text COLLATE latin1_general_ci NOT NULL,
  `conteudo` text COLLATE latin1_general_ci NOT NULL,
  `autor` varchar(100) COLLATE latin1_general_ci NOT NULL,
  `data_publicacao` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `categoria` varchar(100) COLLATE latin1_general_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci;




CREATE TABLE `vaga` (
  `codigo` int(11) NOT NULL,
  `titulo` varchar(255) COLLATE latin1_general_ci NOT NULL,
  `email_recrutador` varchar(100) COLLATE latin1_general_ci NOT NULL,
  `numero_vagas` int(11) DEFAULT NULL,
  `requisito_obrigatorio` mediumtext COLLATE latin1_general_ci NOT NULL,
  `requisito_desejavel` mediumtext COLLATE latin1_general_ci DEFAULT NULL,
  `inicio_expediente` varchar(20) COLLATE latin1_general_ci DEFAULT NULL,
  `fim_expediente` varchar(20) COLLATE latin1_general_ci DEFAULT NULL,
  `salario` float DEFAULT NULL,
  `informacoes_adicionais` mediumtext COLLATE latin1_general_ci NOT NULL,
  `empresa` varchar(55) COLLATE latin1_general_ci DEFAULT NULL,
  `pretensao_salarial` int(11) DEFAULT 0,
  `experiencia` int(11) DEFAULT 0,
  `data_cadastro` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `quantidade_candidatos` int(11) DEFAULT NULL,
  `id_cargo` int(11) DEFAULT NULL,
  `num_contrato` int(11) DEFAULT NULL,
  `codigo_endereco` int(11) DEFAULT NULL,
  `telefone` varchar(15) COLLATE latin1_general_ci DEFAULT NULL,
  `celular` varchar(15) COLLATE latin1_general_ci DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci;





ALTER TABLE `cargo`
  ADD PRIMARY KEY (`id_cargo`);


ALTER TABLE `contrato`
  ADD PRIMARY KEY (`num_contrato`);


ALTER TABLE `endereco`
  ADD PRIMARY KEY (`codigo_endereco`);


ALTER TABLE `posts`
  ADD PRIMARY KEY (`id_posts`);


ALTER TABLE `vaga`
  ADD PRIMARY KEY (`codigo`),
  ADD KEY `id_cargo` (`id_cargo`),
  ADD KEY `num_contrato` (`num_contrato`),
  ADD KEY `codigo_endereco` (`codigo_endereco`);


ALTER TABLE `posts`
  MODIFY `id_posts` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=11;

ALTER TABLE `vaga`
  MODIFY `codigo` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=74;


ALTER TABLE `vaga`
  ADD CONSTRAINT `vaga_ibfk_1` FOREIGN KEY (`id_cargo`) REFERENCES `cargo` (`id_cargo`),
  ADD CONSTRAINT `vaga_ibfk_2` FOREIGN KEY (`num_contrato`) REFERENCES `contrato` (`num_contrato`),
  ADD CONSTRAINT `vaga_ibfk_3` FOREIGN KEY (`codigo_endereco`) REFERENCES `endereco` (`codigo_endereco`);
COMMIT;

